import './App.css';
import React from 'react';



class StarWars extends React.Component {
  constructor(){
    super()
    this.state={
      loadedCharacter: false,
      image: null,
      name: null,
      height: null,
      homeworld: null,
      films: [],
    }
  }
  getNewCharacter(){
    const randomNumber = Math.round(Math.random() *88)
    const url = `raw.githubusercontent.com/akabab/starwars-api/master/api/id/${randomNumber}.json`
    fetch(url)
    console.log(data)
      .then(response =>response.json())
      .then(data => {
        this.setState({
          image: data.image,
          name: data.name,
          height: data.height,
          homeworld: data.homeworld,
          loadedCharacter: true,
    })
   })
  }


  render(){

    return(
      <div>
        {
          this.state.loadedCharacter &&
        <div> 
        <img>{this.state.image}</img> 
        <h1>{this.state.name}</h1>
        <p>{this.state.height} cm</p>
        <p><a href= {this.state.homeworld}>Homeworld</a></p>
        </div>
        } 
        <button type="button" 
        onClick={()=> this.getNewCharacter()} 
        className="btn"
        >Randomize Character</button>
      </div>

    )
  }
}






function App() {
  return (
    <div className="App">
      <header className="App-header">
        <StarWars/>
      </header>
    </div>
  );
}

export default App;
